import React, { useContext } from 'react'
import {Navbar, Container, Nav} from "react-bootstrap"
import logo from "../assets/logo1.png";
import styles from "../styles/NavBar.module.css";
import { NavLink, useNavigate } from "react-router-dom";
import { CurrentUserContext } from '../App';

const NavBar = () => {
    const currentUser = useContext(CurrentUserContext)
    /*const auth = JSON.parse(localStorage.getItem('user'));*/
    const navigate = useNavigate();
    const logout = () =>{
        localStorage.clear();
        navigate('/')
    }
    return (
        <Navbar className={styles.NavBar} expand="md" fixed="top">
            <Container>
                <NavLink to="/">
                <Navbar.Brand>
                    <img src={logo} alt="logo" height="150" />
                </Navbar.Brand></NavLink>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ml-auto text-left">
                        <NavLink
                        exact='true'
                        className={styles.NavLink} activeclassname={styles.Active}
                        to="/"
                        >
                            <i className="fas fa-home"></i> Home
                        </NavLink>
                        {
                            auth ? 
                            <>
                            <NavLink 
                            className={styles.NavLink}
                            activeclassname="active"
                            to={auth ? `/profile/${auth.username}` : "/signin"}>
                                <i className='fas fa-user'></i>{auth.username}
                            </NavLink>
                            <NavLink 
                            className={styles.NavLink} 
                            onClick={logout} 
                            to='/'>
                                <i className="fas fa-sign-in-alt"></i>Sign out
                            </NavLink>
                            </>
                            :<>
                            <NavLink
                            className={styles.NavLink} activeclassname={styles.Active} 
                            to="/signin">
                                <i className="fas fa-sign-out-alt"></i> Sign in
                            </NavLink>
                            <NavLink
                            className={styles.NavLink} activeclassname={styles.Active} 
                            to="/signup">
                                <i className="fas fa-user-plus"></i> Sign up
                            </NavLink>
                            </>
                        }
                    </Nav>
                    {/*<Form inline>
                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                    <Button variant="outline-success">Search</Button>
                    </Form>*/}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}

export default NavBar
