import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";

const ProfileDetail = ({ username }) => {
  const [profile, setProfile] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(
          `/api/profiles/${username}`
        );
        setProfile(response.data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, [username]);

  const handleEditProfile = () => {
    // Redirect user to the edit profile page
    history.push(`/profile/${username}/edit`);
  };

  if (!profile) {
    return <div>Loading profile...</div>;
  }

  return (
    <div>
      <h1>Profile Detail</h1>
      <div>
        <img src={profile.image} alt="Profile" />
        {/* Edit button/icon for profile image */}
        <FontAwesomeIcon icon={faEdit} onClick={handleEditProfile} />
      </div>
      <p>Username: {profile.username}</p>
      <p>About me: {profile.content}</p>
      <p>Followers: {profile.followed}</p>
      <p>Following: {profile.following}</p>
      <h2>Posts</h2>
      {profile.posts.map((post) => (
        <div key={post.id}>
          <p>Title: {post.title}</p>
          <p>Content: {post.content}</p>
        </div>
        ))}
      {/* Display other profile details as needed */}
    </div>
  );
};

export default ProfileDetail;
